        ParticleSystem.prototype.addContact = function (a, b, contacts) {
            // DEBUG: Assert(contacts === this.contactBuffer);
            var flags_data = this.flagsBuffer.data;
            var pos_data = this.positionBuffer.data;
            ///Vec2 d = positionBuffer.data[b] - positionBuffer.data[a];
            var d = b2.Vec2.SubVV(pos_data[b], pos_data[a], ParticleSystem.addContact_s_d);
            var distBtParticlesSq = b2.Vec2.DotVV(d, d);
            if (0 < distBtParticlesSq && distBtParticlesSq < this.squaredDiameter) {
                var invD = b2.invSqrt(distBtParticlesSq);
                ///ParticleContact& contact = contacts.Append();
                var contact = this.contactBuffer.data[this.contactBuffer.append()];
                contact.indexA = a;
                contact.indexB = b;
                contact.flags = flags_data[a] | flags_data[b];
                contact.weight = 1 - distBtParticlesSq * invD * this.inverseDiameter;
                contact.normal.x = invD * d.x;
                contact.normal.y = invD * d.y;
            }
        };
        ParticleSystem.prototype.findContacts_Reference = function (contacts) {
            // DEBUG: Assert(contacts === this.contactBuffer);
            var beginProxy = 0;
            var endProxy = this.proxyBuffer.count;
            this.contactBuffer.count = 0;
            for (var a = beginProxy, c = beginProxy; a < endProxy; a++) {
                var rightTag = ParticleSystem.computeRelativeTag(this.proxyBuffer.data[a].tag, 1, 0);
                for (var b = a + 1; b < endProxy; b++) {
                    if (rightTag < this.proxyBuffer.data[b].tag) {
                        break;
                    }
                    this.addContact(this.proxyBuffer.data[a].index, this.proxyBuffer.data[b].index, this.contactBuffer);
                }
                var bottomLeftTag = ParticleSystem.computeRelativeTag(this.proxyBuffer.data[a].tag, -1, 1);
                for (; c < endProxy; c++) {
                    if (bottomLeftTag <= this.proxyBuffer.data[c].tag) {
                        break;
                    }
                }
                var bottomRightTag = ParticleSystem.computeRelativeTag(this.proxyBuffer.data[a].tag, 1, 1);
                for (var b = c; b < endProxy; b++) {
                    if (bottomRightTag < this.proxyBuffer.data[b].tag) {
                        break;
                    }
                    this.addContact(this.proxyBuffer.data[a].index, this.proxyBuffer.data[b].index, this.contactBuffer);
                }
            }
        };
