                            // connecting the particles a and b at the time of t.
                            // if s is between 0 and 1, c will pass between a and b.
                            ///Vec2 pca = pc - pa;
                            var pca = b2.Vec2.SubVV(pc, pa, s_pca);
                            ///Vec2 vca = vc - va;
                            var vca = b2.Vec2.SubVV(vc, va, s_vca);
