                            // interpolated velocity at the collision point on line ab.
                            ///Vec2 dv = va + s * vba - vc;
                            var dv = s_dv;
                            dv.x = va.x + s * vba.x - vc.x;
                            dv.y = va.y + s * vba.y - vc.y;
