                                    ///cGroup.angularVelocity += Cross(pc - cGroup.GetCenter(), f) / inertia;
                                    cGroup.angularVelocity += b2.Vec2.CrossVV(b2.Vec2.SubVV(pc, cGroup.getCenter(), b2.Vec2.s_t0), f) / inertia;
