            // that means dimensionless density
            ///memset(weightBuffer, 0, sizeof(*weightBuffer) * count);
            for (var k = 0; k < this.count; k++) {
                this.weightBuffer[k] = 0;
            }
            for (var k = 0; k < this.bodyContactBuffer.count; k++) {
                var contact = this.bodyContactBuffer.data[k];
                var a = contact.index;
                var w = contact.weight;
                this.weightBuffer[a] += w;
            }
            for (var k = 0; k < this.contactBuffer.count; k++) {
                var contact = this.contactBuffer.data[k];
                var a = contact.indexA;
                var b = contact.indexB;
                var w = contact.weight;
                this.weightBuffer[a] += w;
                this.weightBuffer[b] += w;
            }
