                var w = contact.weight;
                var m = contact.mass;
                var n = contact.normal;
                var p = pos_data[a];
                var h = this.accumulationBuffer[a] + pressurePerWeight * w;
                ///Vec2 f = velocityPerPressure * w * m * h * n;
                var f = b2.Vec2.MulSV(velocityPerPressure * w * m * h, n, s_f);
