                var a = contact.index;
                var b = contact.body;
                var w = contact.weight;
                var m = contact.mass;
                var n = contact.normal;
                var p = pos_data[a];
                ///Vec2 v = b.GetLinearVelocityFromWorldPoint(p) - velocityBuffer.data[a];
                var v = b2.Vec2.SubVV(b.getLinearVelocityFromWorldPoint(p, b2.Vec2.s_t0), vel_data[a], s_v);
