                    var v = b2.Vec2.SubVV(b.getLinearVelocityFromWorldPoint(p, b2.Vec2.s_t0), vel_data[a], s_v);
                    ///Vec2 f = viscousStrength * m * w * v;
                    var f = b2.Vec2.MulSV(viscousStrength * m * w, v, s_f);
                    ///velocityBuffer.data[a] += GetParticleInvMass() * f;
                    vel_data[a].selfMulAdd(inv_mass, f);
                    ///b.ApplyLinearImpulse(-f, p, true);
