                    var a = contact.indexA;
                    var b = contact.indexB;
                    var w = contact.weight;
                    ///Vec2 v = velocityBuffer.data[b] - velocityBuffer.data[a];
                    var v = b2.Vec2.SubVV(vel_data[b], vel_data[a], s_v);
                    ///Vec2 f = viscousStrength * w * v;
                    var f = b2.Vec2.MulSV(viscousStrength * w, v, s_f);
                    ///velocityBuffer.data[a] += f;
                    vel_data[a].selfAdd(f);
