            for (var k = 0; k < this.contactBuffer.count; k++) {
                var contact = this.contactBuffer.data[k];
                var a = contact.indexA;
                var b = contact.indexB;
                if (this.groupBuffer[a] !== this.groupBuffer[b]) {
                    var w = contact.weight;
                    var n = contact.normal;
                    var h = this.depthBuffer[a] + this.depthBuffer[b];
                    var f = b2.Vec2.MulSV(ejectionStrength * h * w, n, s_f);
                    vel_data[a].selfSub(f);
                    vel_data[b].selfAdd(f);
                }
            }
