            var s_n = ParticleSystem.removeSpuriousBodyContacts_s_n;
            var s_pos = ParticleSystem.removeSpuriousBodyContacts_s_pos;
            var s_normal = ParticleSystem.removeSpuriousBodyContacts_s_normal;
